          className="jumbotron text-center A1">
          <h1> REACT 
            <span className="A2" >NATIVE</span>
          </h1>
          <p
           className="A3" >UI of some app that i have created in past ‚ù§</p>
          <p></p>
        </div>
      </section></div>
    <div className="container">
    <div id="carousel">

{
    data.map((e,i)=>{
        return (
            <div class={e}>
            <img src={"static/img/"+(i+1)+".png"}/>
          </div>
        )
    })
}

